create table vacancies (is_response boolean, response integer, contact_information_id bigint unique, creation_date timestamp(6), employer_id bigint, id bigserial not null, position_id bigint, resume_id bigint unique, salary_id bigint unique, about_company varchar(3000), additional_information varchar(255), description varchar(3000), experience varchar(255), industry varchar(255), skills varchar(255), status_of_vacancy varchar(255) check (status_of_vacancy in ('OPEN','ARCHIVE','CLOSED')), type_of_employments varchar(255) check (type_of_employments in ('POLNIY_RABOCHIY_DEN','NEPOLNIY_RABOCHIY_DEYN','UDALENNAYA_RABOTA')), primary key (id))
;